import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { listProductForYou } from "../../Redux/Action/ProductAction";
import { Link } from "react-router-dom";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const ProductRecommendations = () => {
  const dispatch = useDispatch();
  const productListForYou = useSelector((state) => state.productListForYou);
  const { products } = productListForYou;

  useEffect(() => {
    dispatch(listProductForYou());
  }, [dispatch]);
  var settings = {
    dots: true,
    infinite: false,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 4,
    initialSlide: 0,
    autoplay: true,
    autoplaySpeed: 2000,
    pauseOnHover: true,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          initialSlide: 2,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };
  return (
    <div className="container my-5">
      <h3 className="mb-4 text-center">Recommended Products</h3>
      <div class="card-deck">
        <Slider {...settings}>
          {products.map((product) => (
            <div key={product.id} class="card">
              <img
                src={product.imageUrl}
                alt={product.name}
                class="card-img-top"
              />
              <div class="card-body">
                <h5 class="card-title">{product.name}</h5>
                <p class="card-text">{`$${product.price}`}</p>
                <Link to={`/products/${product.id}`} class="btn btn-primary">
                  View Product
                </Link>
              </div>
            </div>
          ))}
        </Slider>
      </div>
    </div>
  );
};

export default ProductRecommendations;
