import React, { useEffect, useRef, useState } from "react";
import Rating from "./Rating";
import Pagination from "./pagination";
import { useDispatch, useSelector } from "react-redux";
import { listProduct } from "../../Redux/Action/ProductAction";
import Loading from "../LoadingError/Loading";
import Message from "../LoadingError/Error";
import { getUserDetails } from "../../Redux/Action/UserAction";
import { listCart } from "../../Redux/Action/CartAction";
import { listBrands, listCategories } from "../../Redux/Action/FilterAction";
import { Link } from "react-router-dom";

const ShopSection = (props) => {
  const dispatch = useDispatch();

  const productList = useSelector((state) => state.productList);
  const { loading, products, error, pageTotal } = productList;

  const categoryList = useSelector((state) => state.categoryList);
  const { categories } = categoryList;

  const brandList = useSelector((state) => state.brandList);
  const { brands } = brandList;
  const search = "";
  const { pageId } = props;

  const [selectedCategories, setSelectedCategories] = useState(null);
  const [selectedBrands, setSelectedBrands] = useState(null);
  const [selectedSorts, setsSlectedSorts] = useState(null);
  const [priceFrom, setPriceFrom] = useState(null);
  const [priceTo, setPriceTo] = useState(null);

  const [hasPrice, setHasPrice] = useState(false); // biến state để kiểm tra người dùng đã nhập giá hay chưa
  const hasPriceRef = useRef(hasPrice); // sử dụng useRef để lưu giá trị của biến hasPrice
  useEffect(() => {
    hasPriceRef.current = hasPrice; // lưu giá trị mới nhất của hasPrice vào useRef
  }, [hasPrice]);
  const handleSubmit = (event) => {
    event.preventDefault();
    if (priceFrom !== null && priceTo !== null) {
      // kiểm tra nếu người dùng đã nhập giá
      setHasPrice(true); // cập nhật biến state hasPrice thành true khi người dùng ấn nút "Check"
    }
  };
  useEffect(() => {
    if (hasPriceRef.current) {
      // sử dụng giá trị hiện tại của hasPrice từ useRef
      dispatch(
        listProduct(
          search,
          priceFrom,
          priceTo,
          selectedSorts,
          selectedCategories,
          selectedBrands,
          pageId
        )
      );
    } else {
      dispatch(
        listProduct(
          search,
          selectedSorts,
          selectedCategories,
          selectedBrands,
          pageId
        )
      );
    }
    dispatch(getUserDetails());
    dispatch(listCart());
  }, [
    dispatch,
    search,
    priceFrom,
    priceTo,
    selectedSorts,
    selectedCategories,
    selectedBrands,
    pageId,
    hasPriceRef.current, // sử dụng giá trị hiện tại của hasPrice từ useRef trong danh sách dependencies
  ]);
  useEffect(() => {
    dispatch(listCategories());
    dispatch(listBrands());
  }, []);

  return (
    <>
      <div className="container">
        <div className="section">
          <div className="row">
            <div className="col-lg-3 col-md-3">
              <div className="price-box">
                <h3>Price</h3>
                <form
                  className="d-flex w-100 flex-column from-price "
                  onSubmit={handleSubmit} // sử dụng hàm handleSubmit để xử lý sự kiện submit
                >
                  <div className="d-flex w-100 justify-content-between from-price ">
                    <input
                      type="input"
                      name="from"
                      placeholder="Price from"
                      value={priceFrom}
                      onChange={(e) => setPriceFrom(e.target.value)}
                      className="input-price"
                    />
                    <input
                      type="input"
                      name="to"
                      placeholder="Price to"
                      value={priceTo}
                      onChange={(e) => setPriceTo(e.target.value)}
                      className="input-price"
                    />
                  </div>
                  <button type="submit" className="w-100">
                    Check
                  </button>
                </form>
              </div>
            </div>

            <div className="col-lg-9 col-md-9 article">
              <div className="shopcontainer row">
                {loading ? (
                  <Loading />
                ) : error ? (
                  <Message variant="alert-danger">{error}</Message>
                ) : (
                  <>
                    {products.map((product) => (
                      <div
                        className="shop col-lg-4 col-md-6 col-sm-6"
                        key={product.id}
                      >
                        <div className="border-product">
                          <Link to={`/products/${product.id}`}>
                            <div className="shopBack">
                              <img src={product.imageUrl} alt={product.name} />
                            </div>
                          </Link>

                          <div className="shoptext">
                            <p>
                              <Link to={`/products/${product.id}`}>
                                {product.name}
                              </Link>
                            </p>

                            <Rating
                              value={product.averageRating}
                              text={`${product.reviewQuantity} reviews`}
                            />
                            <h3>${product.price}</h3>
                          </div>
                        </div>
                      </div>
                    ))}
                  </>
                )}

                {/* Pagination */}
                <Pagination
                  currentPage={pageId}
                  pageTotal={pageTotal}
                  // search={search ? search : ""}
                  // categoryId={categoryId}
                  // brandId={brandId}
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default ShopSection;
