import React, { useState, useEffect } from "react";
import axios from "axios";
import { useDispatch } from "react-redux";
import { listProductForYou } from "../../Redux/Action/ProductAction";

const ProductRecommendations = ({ productId }) => {
  const dispatch = useDispatch();
  const listProductForYou = useSelector((state) => state.listProductForYou);
  const { productForYou } = listProductForYou;
  useEffect(() => {
    dispatch(listProductForYou());
  });
  return (
    <div className="container my-5">
      <h3 className="mb-4">Recommended Products</h3>
      <div className="row">
        {productForYou.map((product) => (
          <div key={product.id} className="col-md-4 mb-4">
            <div className="card h-100">
              <img
                src={product.imageUrl}
                alt={product.name}
                className="card-img-top"
              />
              <div className="card-body">
                <h5 className="card-title">{product.name}</h5>
                <p className="card-text">{product.description}</p>
                <p className="card-text">{`$${product.price}`}</p>
                <Link
                  to={`/products/${product.id}`}
                  className="btn btn-primary"
                >
                  View Product
                </Link>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductRecommendations;
