import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { listProductForYou } from "../../Redux/Action/ProductAction";
import { Link } from "react-router-dom";
import Slider from "react-slick";

const ProductRecommendations = () => {
  const dispatch = useDispatch();
  const productListForYou = useSelector((state) => state.productListForYou);
  const { products } = productListForYou;

  useEffect(() => {
    dispatch(listProductForYou());
  }, [dispatch]);
  var settings = {
    dots: true,
    infinite: false,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 4,
    initialSlide: 0,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          initialSlide: 2,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };
  return (
    <div className="container my-5">
      <h3 className="mb-4">Recommended Products</h3>
      <Slider {...settings}>
        <div className="col-md-4 mb-4">
          <div className="card h-100">
            <img
              src=" https://cdn2.cellphones.com.vn/358x358,webp,q100/media/catalog/product/t/_/t_m_12.png "
              alt=" Ip12 "
              className="card-img-top"
            />
            <div className="card-body">
              <h5 className="card-title">ip12</h5>
              <p className="card-text">
                iPhone 14 Pro Max là mẫu flagship nổi bật nhất của Apple trong
                lần trở lại năm 2022 với nhiều cải tiến về công nghệ cũng như vẻ
                ngoài cao cấp, sang chảnh hợp với gu thẩm mỹ đại chúng. Những
                chiếc điện thoại đến từ nhà Táo Khuyết nhận được rất nhiều sự kỳ
                vọng của thị trường ngay từ khi chưa ra mắt. Vậy liệu những
                chiếc flagship đến từ công ty công nghệ hàng đầu thế giới này có
                làm bạn thất vọng? Cùng khám phá những điều thú vị về iPhone 14
                Pro Max ở bài viết dưới đây nhé.
              </p>
              <p className="card-text">100000VND</p>
            </div>
          </div>
        </div>

        {/* {products.map((product) => (
          <div key={product.id} className="card h-100">
            <img
              src={product.imageUrl}
              alt={product.name}
              className="card-img-top"
            />
            <div className="card-body">
              <h5 className="card-title">{product.name}</h5>
              <p className="card-text">{product.description}</p>
              <p className="card-text">{`$${product.price}`}</p>
              <Link to={`/products/${product.id}`} className="btn btn-primary">
                View Product
              </Link>
            </div>
          </div>
        ))} */}
      </Slider>
    </div>
  );
};

export default ProductRecommendations;
