import React, { useEffect, useState } from "react";
import Header from "./../components/Header";
import Rating from "../components/homeComponents/Rating";
import { Link } from "react-router-dom";
import Message from "./../components/LoadingError/Error";
import { useDispatch, useSelector } from "react-redux";
import { listProductDeTails } from "../Redux/Action/ProductAction";
import Loading from "../components/LoadingError/Loading";
import { addToCart2, listCart } from "../Redux/Action/CartAction";
import { toast } from "react-toastify";
import Toast from "../components/LoadingError/Toast";
import { CART_ADD_ITEM_SUCCESS } from "../Redux/Constants/CartConstants";

const SingleProduct = ({ history, match }) => {
  const [quantity, setQuantity] = useState(1);
  const productId = match.params.id;
  const dispatch = useDispatch();
  const toastId = React.useRef(null);
  const toastObject = {
    pauseOnFocusLoss: false,
    draggable: false,
    pauseOnHover: false,
    autoClose: 2000,
  };

  const productDetails = useSelector((state) => state.productDetails);
  const { loading, error, product } = productDetails;

  const cartList = useSelector((state) => state.cartList);
  const { cartLists } = cartList;

  const AddToCartHanddle = (e) => {
    e.preventDefault();
    dispatch(addToCart2(productId, quantity));
    if (dispatch({ type: CART_ADD_ITEM_SUCCESS })) {
      if (cartLists.some((item) => item.productId === productId)) {
        toastId.current = toast.success(
          // `${cartLists.map((item) => item.message)}`,
          "Sản phẩm đã có, số lượng được cập nhật",
          toastObject
        );
      } else {
        toastId.current = toast.success(
          // `${cartLists.map((item) => item.message)}`,
          "Đã thêm sản phẩm vào giỏ hàng",
          toastObject
        );
      }
    }
  };

  useEffect(() => {
    dispatch(listProductDeTails(productId));
    dispatch(listCart());
  }, [dispatch, productId]);

  return (
    <>
      <Header />
      <div className="container single-product">
        <Toast />
        {loading ? (
          <Loading />
        ) : error ? (
          <Message variant="alert-danger">{error}</Message>
        ) : (
          <>
            <div className="row">
              <div className="col-md-6">
                <div className="single-image">
                  <img src={product.imageUrl} alt={product.name} />
                </div>
              </div>
              <div className="col-md-6">
                <div className="product-dtl">
                  <div className="product-info">
                    <div className="product-name">{product.name}</div>
                  </div>
                  <p>{product.description}</p>

                  <div className="product-count col-lg-7 ">
                    <div className="flex-box d-flex justify-content-between align-items-center">
                      <h6>Price</h6>
                      <span>${product.price}</span>
                    </div>
                    <div className="flex-box d-flex justify-content-between align-items-center">
                      <h6>Status</h6>
                      {product.quantity}
                    </div>
                    <div className="flex-box d-flex justify-content-between align-items-center">
                      <h6>Reviews</h6>
                      <Rating
                        value={product.averageRating}
                        text={`${product.reviewQuantity} reviews`}
                      />
                    </div>
                    {product.quantity > 0 ? (
                      <>
                        <div className="flex-box d-flex justify-content-between align-items-center">
                          <h6>Quantity</h6>
                          <select
                            value={quantity}
                            onChange={(e) => setQuantity(e.target.value)}
                          >
                            {[...Array(product.quantity).keys()].map((x) => (
                              <option key={x + 1} value={x + 1}>
                                {x + 1}
                              </option>
                            ))}
                          </select>
                        </div>
                        <button
                          className="round-black-btn"
                          onClick={AddToCartHanddle}
                        >
                          Add To Cart
                        </button>
                      </>
                    ) : null}
                  </div>
                </div>
              </div>
            </div>

            {/* RATING */}
            <div className="row my-5">
              <div className="col-md-6">
                <h6 className="mb-3">REVIEWS</h6>
                <Message variant={"alert-info mt-3"}>No Reviews</Message>
                <div className="mb-5 mb-md-3 bg-light p-3 shadow-sm rounded">
                  <strong>Admin Doe</strong>
                  <Rating />
                  <span>Jan 12 2021</span>
                  <div className="alert alert-info mt-3">
                    Lorem Ipsum is simply dummy text of the printing and
                    typesetting industry. Lorem Ipsum has been the industry's
                    standard dummy text ever since the 1500s, when an unknown
                    printer took a galley of type and scrambled it to make a
                    type specimen book
                  </div>
                </div>
              </div>
              <div className="col-md-6">
                <h6>WRITE A CUSTOMER REVIEW</h6>
                <div className="my-4"></div>

                <form>
                  <div className="my-4">
                    <strong>Rating</strong>
                    <select className="col-12 bg-light p-3 mt-2 border-0 rounded">
                      <option value="">Select...</option>
                      <option value="1">1 - Poor</option>
                      <option value="2">2 - Fair</option>
                      <option value="3">3 - Good</option>
                      <option value="4">4 - Very Good</option>
                      <option value="5">5 - Excellent</option>
                    </select>
                  </div>
                  <div className="my-4">
                    <strong>Comment</strong>
                    <textarea
                      row="3"
                      className="col-12 bg-light p-3 mt-2 border-0 rounded"
                    ></textarea>
                  </div>
                  <div className="my-3">
                    <button className="col-12 bg-black border-0 p-3 rounded text-white">
                      SUBMIT
                    </button>
                  </div>
                </form>
                <div className="my-3">
                  <Message variant={"alert-warning"}>
                    Please{" "}
                    <Link to="/login">
                      " <strong>Login</strong> "
                    </Link>{" "}
                    to write a review{" "}
                  </Message>
                </div>
              </div>
            </div>
          </>
        )}
      </div>
    </>
  );
};

export default SingleProduct;
