import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { listProductForYou } from "../../Redux/Action/ProductAction";
import { Link } from "react-router-dom";
import Slider from "react-slick";

const ProductRecommendations = () => {
  const dispatch = useDispatch();
  const productListForYou = useSelector((state) => state.productListForYou);
  const { products } = productListForYou;

  useEffect(() => {
    dispatch(listProductForYou());
  }, [dispatch]);

  return (
    <div className="container my-5">
      <h3 className="mb-4">Recommended Products</h3>
      <div
        id="product-carousel"
        className="carousel slide"
        data-bs-ride="carousel"
      >
        <div className="carousel-inner">
          {products &&
            products.map((product, index) => (
              <div
                key={product.id}
                className={`carousel-item ${index === 0 ? "active" : ""}`}
              >
                <div className="row">
                  <div className="col-md-4 mb-4">
                    <div className="card h-100">
                      <img
                        src={product.imageUrl}
                        alt={product.name}
                        className="card-img-top"
                      />
                      <div className="card-body">
                        <h5 className="card-title">{product.name}</h5>
                        <p className="card-text">{product.description}</p>
                        <p className="card-text">{`$${product.price}`}</p>
                        <Link
                          to={`/products/${product.id}`}
                          className="btn btn-primary"
                        >
                          View Product
                        </Link>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
        </div>
        <button
          className="carousel-control-prev"
          type="button"
          data-bs-target="#product-carousel"
          data-bs-slide="prev"
        >
          <span
            className="carousel-control-prev-icon"
            aria-hidden="true"
          ></span>
          <span className="visually-hidden">Previous</span>
        </button>
        <button
          className="carousel-control-next"
          type="button"
          data-bs-target="#product-carousel"
          data-bs-slide="next"
        >
          <span
            className="carousel-control-next-icon text-danger"
            aria-hidden="true"
          ></span>
          <span className="visually-hidden">Next</span>
        </button>
      </div>
    </div>
  );
};

export default ProductRecommendations;
